var list = [];
if (result.match("开始浏览") == null) { //判断目录
    var regex = /href="(content-.+-\d+.html)">(.*)<\/a>/g;
    var match;
    while ((match = regex.exec(result)) !== null) {
        list.push({
            m: match[1],
            n: match[2]
        })
    }
} else {
    var regex = /href="(.*)" s.*(开始浏览)/;
    var match = result.match(regex);
    list.push({
        m: match[1],
        n: match[2]
    });
}
